//App.js

import React, { useState } from 'react';
import './App.css';
import SearchComponent from './components/SearchComponent';
import ShowCourseComponent from './components/ShowCourseComponent';
import UserCartComponent from './components/UserCartComponent';



function App() {
	const [courses,] = useState([
		{
			id: 1,
			name: 'Arts Sketch',
			price: 5000,
			image:
				'data:image/jpeg;base64,'
		},
		{
			id: 2,
			name: 'Light Art',
			price: 6999,
			image:
				'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT6czvsYUCSGc6ItCrizaD5HbC7MtTXzgblCw&usqp=CAU'
		},
		{
			id: 3,
			name: 'Look UP Sktec',
			price: 5559,
			image:
				'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRE5D__45suAPg8oQ9Moa9GgwVDK3EldPlhHArzk1yKL8SbGH9OeL10D_KzrpMp9L0hv70&usqp=CAU'
		},
		{
			id: 4,
			name: 'Creative Spirit Sketch',
			price: 3999,
			image:
				'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTaUPYHwofKKjg6bgCIc9CiifL_bMtvcUsci2bA-MPcFIu8z32DZEH_pTm1YPH3xjcnnIY&usqp=CAU'
		},
		{
			id: 5,
			name: 'Lion Sktech',
			price: 2000,
			image:
				'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQPvk_ydUJVglr8gslTSBeQEfPs96dwq8MA5Y4Lg-jAMuLndtKhasR7uAWqMawosaGYhkM&usqp=CAU'
		},
		{
			id: 6,
			name: 'Queens Sketch',
			price: 7999,
			image:
				'data:image/jpeg;base64,'
		},
	]);

	const [cartCourses, setCartCourses] = useState([]);
	const [searchCourse, setSearchCourse] = useState('');

	const addCourseToCartFunction = (GFGcourse) => {
		const alreadyCourses = cartCourses
			.find(item => item.product.id === GFGcourse.id);
		if (alreadyCourses) {
			const latestCartUpdate = cartCourses.map(item =>
				item.product.id === GFGcourse.id ? {
					...item, quantity: item.quantity + 1
				}
					: item
			);
			setCartCourses(latestCartUpdate);
		} else {
			setCartCourses([...cartCourses, { product: GFGcourse, quantity: 1 }]);
		}
	};

	const deleteCourseFromCartFunction = (GFGCourse) => {
		const updatedCart = cartCourses
			.filter(item => item.product.id !== GFGCourse.id);
		setCartCourses(updatedCart);
	};

	const totalAmountCalculationFunction = () => {
		return cartCourses
			.reduce((total, item) =>
				total + item.product.price * item.quantity, 0);
	};

	const courseSearchUserFunction = (event) => {
		setSearchCourse(event.target.value);
	};

	const filterCourseFunction = courses.filter((course) =>
		course.name.toLowerCase().includes(searchCourse.toLowerCase())
	);


	return (
		<div className="App">
			<SearchComponent searchCourse={searchCourse}
				courseSearchUserFunction=
				{courseSearchUserFunction} />
			<main className="App-main">
				<ShowCourseComponent
					courses={courses}
					filterCourseFunction={filterCourseFunction}
					addCourseToCartFunction={addCourseToCartFunction}
				/>

				<UserCartComponent
					cartCourses={cartCourses}
					deleteCourseFromCartFunction={deleteCourseFromCartFunction}
					totalAmountCalculationFunction={
						totalAmountCalculationFunction
					}
					setCartCourses={setCartCourses}
				/>
			</main>
			<footer className="footer">
				<p>Created by Prathamesh</p>

			</footer>

		</div>


	);
}

export default App;
